	<script type="text/javascript">
		$('.init button').click(function(){
			$('.init').addClass('hide');
			$('.convert').removeClass('hide');
		});
		$('.navigation-bar-toggle').click(function(){
			if(!$('.navigation-bar-wrapper').is(':visible')){
				$('.navigation-bar-wrapper').css({"display":"block"});
			} else{
				$('.navigation-bar-wrapper').css({"display":"none"});
			}
		})
		var re = /^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i;
		var re1 = /^[789]\d{9}$/i;	
		$(function(){
			$('#convertform').submit(function(e){
				e.preventDefault();
				var email = $('#convert_email').val();
				var name = $('#convert_name').val();
				var mobile = $('#convert_mobile').val();
				var flag = false;
				if(name == ''){
					$('#convert_name').parents('.input-group').find('.input-group-addon').css({"border-color":"#f00"});
					$('#convert_name').parents('.input-group').find('.input-group-addon').css({"background-color":"#f00"});
					$('#convert_name').css({"border-color":"#f00","color":"#f00"}).val('Please enter correct name.');
					setTimeout(function(){
						$('#convert_name').val('').css({"color":"#717a8f"});	;
					},2000);
					flag = true;								
				}
				if(re.test(email) == false){
					$('#convert_email').parents('.input-group').find('.input-group-addon').css({"border-color":"#f00"});
					$('#convert_email').parents('.input-group').find('.input-group-addon').css({"background-color":"#f00"});
					$('#convert_email').css({"border-color":"#f00","color":"#f00"}).val('Please enter correct email.');
					setTimeout(function(){
						$('#convert_email').val(email).css({"color":"#717a8f"});	;
					},2000);
					flag = true;								
				}	
				if(re1.test(mobile) == false){
					$('#convert_mobile').parents('.input-group').find('.input-group-addon').css({"border-color":"#f00"});
					$('#convert_mobile').parents('.input-group').find('.input-group-addon').css({"background-color":"#f00"});
					$('#convert_mobile').css({"border-color":"#f00","color":"#f00"}).val('Please enter correct mobile number.');
					setTimeout(function(){
						$('#convert_mobile').val(mobile).css({"color":"#717a8f"});	;
					},2000);
					flag = true;							
				}
				if(!flag){
					var offsetIST = 5.5;
					var d=new Date();
					var utcdate =  new Date(d.getTime() + (d.getTimezoneOffset()*60000));
					var istdate =  new Date(utcdate.getTime() - ((-offsetIST*60)*60000));			
					var firstName = name.split(' ').slice(0, -1).join(' ');
					var lastName = name.split(' ').slice(-1).join(' ');
					if(firstName == ''){
						firstName = lastName;
						lastName = '';
					}					
					mixpanel.alias(email);		        				
					mixpanel.identify(email);
					mixpanel.people.set({
						"$name": name,
						"$first_name": firstName,
						"$last_name": lastName,
						"$email": email,
						"$phone": mobile,
						"$profile_type":"Landing Page Signup",
						"$user_type": "Student",
						"$signup_time": istdate,
						"$signup_category": "<?php echo $title; ?>",
						"$ip": "<?php echo $_SERVER['REMOTE_ADDR']; ?>"
					});																					
					$.ajax({																
						url: '<?php echo get_site_url();?>/easysignup/send.php',
						type: 'POST',
						contentType: "application/x-www-form-urlencoded; charset=UTF-8",
						dataType: "text",
						data: { 'category': '<?php echo $title; ?>', 'name': name, 'email': email, 'mobile': mobile },
						error: function(){
							alert('ERROR');
						},
						success: function(resdata) {
							if(resdata.indexOf("ERR:") == -1){
								$('.convert').addClass('hide');
								$('.signupcta h2').addClass('hide');
								$('.converted').removeClass('hide');
							} else{
								alert('ERROR');
							}
						},
						cache: false
					});		
				}									

			})
	})
		setInterval(function(){
			var email = $('#convert_email').val();
			var name = $('#convert_name').val();
			var mobile = $('#convert_mobile').val();
			if(name != '' && name != 'Please enter correct name.'){
				$('#convert_name').parents('.input-group').find('.input-group-addon').css({"border-color":"#77c04b"});
				$('#convert_name').parents('.input-group').find('.input-group-addon').css({"background-color":"#77c04b"});
				$('#convert_name').css({"border-color":"#ebeef1","color":"#717a8f"});								
			}			
			if(re.test(email) == true){
				$('#convert_email').parents('.input-group').find('.input-group-addon').css({"border-color":"#77c04b"});
				$('#convert_email').parents('.input-group').find('.input-group-addon').css({"background-color":"#77c04b"});
				$('#convert_email').css({"border-color":"#ebeef1","color":"#717a8f"});								
			}			
			if(re1.test(mobile) == true){
				$('#convert_mobile').parents('.input-group').find('.input-group-addon').css({"border-color":"#77c04b"});
				$('#convert_mobile').parents('.input-group').find('.input-group-addon').css({"background-color":"#77c04b"});
				$('#convert_mobile').css({"border-color":"#ebeef1","color":"#717a8f"});								
			}
		}, 100);			
	</script>
	</html>